import{_ as a,c as e,o as r,a2 as t}from"./chunks/framework.DEnmJWvs.js";const l="/assets/TDU21Adv_WebBanner.Bfvkkc2a.png",f=JSON.parse('{"title":"TDU21 Advent Calendar 2021 概要","description":"","frontmatter":{},"headers":[],"relativePath":"advent-calendar-2021.md","filePath":"advent-calendar-2021.md"}'),n={name:"advent-calendar-2021.md"},i=t('<h1 id="tdu21-advent-calendar-2021-概要" tabindex="-1">TDU21 Advent Calendar 2021 概要 <a class="header-anchor" href="#tdu21-advent-calendar-2021-概要" aria-label="Permalink to &quot;TDU21 Advent Calendar 2021 概要&quot;">​</a></h1><p><img src="'+l+'" alt="WebBanner"></p><h2 id="開催趣旨" tabindex="-1">開催趣旨 <a class="header-anchor" href="#開催趣旨" aria-label="Permalink to &quot;開催趣旨&quot;">​</a></h2><p>TDU21期生で Advent Calendar を書こうという企画です。<br> テーマは<strong>自由</strong>です。自分の好きなものや語りたいことなどを自由に書けたら良いと思います！</p><h3 id="advent-calendar-とは" tabindex="-1">Advent Calendar とは？ <a class="header-anchor" href="#advent-calendar-とは" aria-label="Permalink to &quot;Advent Calendar とは？&quot;">​</a></h3><p>クリスマスまでのカウントダウンの間、テーマに沿ってブログ等に記事を投稿する企画です。</p><p>参考：<a href="https://ja.wikipedia.org/wiki/%E3%82%A2%E3%83%89%E3%83%99%E3%83%B3%E3%83%88%E3%82%AB%E3%83%AC%E3%83%B3%E3%83%80%E3%83%BC" target="_blank" rel="noreferrer">アドベントカレンダー - Wikipedia</a></p><h2 id="参加要件" tabindex="-1">参加要件 <a class="header-anchor" href="#参加要件" aria-label="Permalink to &quot;参加要件&quot;">​</a></h2><ul><li>TDU21期生であること</li><li>TDU21 非公式Discordキャンパスで学籍認証を行っていること</li></ul><h2 id="開催日程" tabindex="-1">開催日程 <a class="header-anchor" href="#開催日程" aria-label="Permalink to &quot;開催日程&quot;">​</a></h2><h3 id="エントリー受付" tabindex="-1">エントリー受付 <a class="header-anchor" href="#エントリー受付" aria-label="Permalink to &quot;エントリー受付&quot;">​</a></h3><p>2021年11月7日(日)～<br> ※前倒しで公開しました！</p><h3 id="公開" tabindex="-1">公開 <a class="header-anchor" href="#公開" aria-label="Permalink to &quot;公開&quot;">​</a></h3><p>2021年12月1日(水)～25日(土)</p><h2 id="参加方法" tabindex="-1">参加方法 <a class="header-anchor" href="#参加方法" aria-label="Permalink to &quot;参加方法&quot;">​</a></h2><p>Discord キャンパス上で参加申し込みリンクを掲示していますので、フォームに必要事項を入力して送信してください。<br> 申し込み後、案内があり次第アドベントカレンダーを管理するサービス上での登録をお願いします。</p><h2 id="注意" tabindex="-1">注意 <a class="header-anchor" href="#注意" aria-label="Permalink to &quot;注意&quot;">​</a></h2><ul><li>使用するサービスの都合上、<strong>Twitter アカウントが必要になります</strong>ので、ご用意をお願いします</li><li><strong>ご自身のブログが必要となります</strong>。お持ちでない方は、開設をお願いします</li><li>公序良俗に反する内容や、誹謗中傷などは禁止です。常識的な内容を心がけてください <ul><li>著作権にも十分に気を配ってください</li><li><a href="/rule.html">キャンパスルール</a>も参考に</li></ul></li><li>12月は期末テストなどが実施され、執筆時期と被る可能性があります。学業に影響しない範囲で楽しみましょう</li><li>その他不明点があればモデレーターまでお問い合わせください</li></ul><h2 id="推奨するブログサービス" tabindex="-1">推奨するブログサービス <a class="header-anchor" href="#推奨するブログサービス" aria-label="Permalink to &quot;推奨するブログサービス&quot;">​</a></h2><ul><li>はてなブログ (<a href="https://hatenablog.com/" target="_blank" rel="noreferrer">https://hatenablog.com</a>) <ul><li>デザインが豊富</li></ul></li><li>note (<a href="https://note.com/" target="_blank" rel="noreferrer">https://note.com</a>) <ul><li>デザインがシンプルで見やすい</li></ul></li></ul>',20),o=[i];function d(h,s,c,u,p,_){return r(),e("div",null,o)}const m=a(n,[["render",d]]);export{f as __pageData,m as default};
