import{_ as s,o as t,c as e,O as o}from"./chunks/framework.0401b637.js";const a="/assets/discord-create.50573acd.png",r="/assets/discord-code-form.0bbd6e2f.png",p="/assets/bot-message-notify.56688117.png",c="/assets/first-step.f03f05b2.png",i="/assets/mail.3975fca1.png",n="/assets/second-step.649cc072.png",d="/assets/third-step.cbbb6ca3.png",l="/assets/channel-list.83ea9d91.png",h="/assets/result-profile.70a041d0.png",q=JSON.parse('{"title":"グループに参加する","description":"","frontmatter":{},"headers":[],"relativePath":"howtojoin.md","filePath":"howtojoin.md"}'),m={name:"howtojoin.md"},_=o('<h1 id="グループに参加する" tabindex="-1">グループに参加する <a class="header-anchor" href="#グループに参加する" aria-label="Permalink to &quot;グループに参加する&quot;">​</a></h1><div class="danger custom-block"><p class="custom-block-title">DANGER</p><p>グループへの参加にあたっては事前に<a href="/rule.html">ルール</a>を確認してください。<br> 参加した時点でルールに同意したものとみなします。</p></div><h2 id="step-1-discordのアカウントを作成する" tabindex="-1">Step.1 - Discordのアカウントを作成する <a class="header-anchor" href="#step-1-discordのアカウントを作成する" aria-label="Permalink to &quot;Step.1 - Discordのアカウントを作成する&quot;">​</a></h2><p><a href="https://support.discord.com/hc/ja/articles/360033931551-%E3%81%AF%E3%81%98%E3%82%81%E3%81%AB" target="_blank" rel="noreferrer">はじめに - Discord</a> を参考に、Discordのアカウントを作成してください。</p><h2 id="step-2-サーバーに参加する" tabindex="-1">Step.2 - サーバーに参加する <a class="header-anchor" href="#step-2-サーバーに参加する" aria-label="Permalink to &quot;Step.2 - サーバーに参加する&quot;">​</a></h2><p><a href="/discord.html">こちら</a>をクリックするか、アプリの左側のメニューの＋ボタンを押して出てくる画面に招待コード <code>cSvGt2uuMf</code> を入力してサーバーに参加してください。</p><p><img src="'+a+'" style="max-height:300px;"><br><img src="'+r+'" style="max-height:300px;"></p><h2 id="step-3-学籍認証を行う" tabindex="-1">Step.3 - 学籍認証を行う <a class="header-anchor" href="#step-3-学籍認証を行う" aria-label="Permalink to &quot;Step.3 - 学籍認証を行う&quot;">​</a></h2><p>参加すると、自動的に「TDU21-DiscordBot」よりメッセージが届きます。</p><p><img src="'+p+'" alt="bot-message-notify.png"></p><p>通知が出ていない場合は、<strong>一番左上の Discordのロゴ (ウンパスくん) をクリックしたメニューの中に表示</strong>されています。</p><p>届いているメッセージをご確認いただいたうえで、チャットに学籍番号を入力します。<br> 学科の英語部分は<strong>大文字小文字どちらでも構いません</strong>。</p><img src="'+c+'" style="max-height:500px;"><p>送信後、あなたの学校メールアドレス ( <code>[学籍番号]@ms.dendai.ac.jp</code> ) に 「TDU21 非公式Discordキャンパス」(<code>noreply@tdu21-discord.org</code>) より<strong>メールが届きます</strong>。</p><p><img src="'+i+'" alt="mail.png"></p><div class="tip custom-block"><p class="custom-block-title">「画像の表示」について</p><p>使用しているシステムにてメールの開封確認が導入されているため、画像の表示を求められますが、許可の有無によって影響はありませんので許可しなくて構いません。</p></div><p>メール内に記載されている<strong>4ケタの番号をチャットに入力</strong>してください。</p><img src="'+n+'" style="max-height:300px;"><p>チャットに認証番号を送信後に「認証完了！」と表示されれば認証は完了です！</p><img src="'+d+'" style="max-height:500px;"><p>元のDiscordキャンパスに戻ると、チャンネルの表示が増え、あなたのプロフィールに学部・学科・偶数奇数の表示が追加されています。</p><p><img src="'+l+'" style="max-height:500px;"><br></p><img src="'+h+'"><p><strong>以上で認証は終了です。</strong></p>',24),g=[_];function f(b,u,x,D,S,k){return t(),e("div",null,g)}const P=s(m,[["render",f]]);export{q as __pageData,P as default};
